import React, { useCallback } from "react";
import { useDropzone } from "react-dropzone";
import "./Dropbox.css";

function MyDropzone() {
  const onDrop = useCallback((acceptedFiles) => {
    // Do something with the files
    function product(acceptedFiles) {
      return acceptedFiles;
    }
    product(acceptedFiles);
    console.log("file obtained", acceptedFiles);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  return (
    document.querySelector("body").addEventListener("mousemove", eyeball),
    (
      <section>
        <div className="dropbox" {...getRootProps()}>
          <input {...getInputProps()} />
          {isDragActive ? (
            <p>Drop the files here ...</p>
          ) : (
            <p>Drag 'n' drop some files here, or click to select files</p>
          )}

          <div>
            <div className="minion">
              <div class="goggle-strap left"></div>
              <div class="goggle-strap right"></div>
              <div class="goggle-strap-link left"></div>
              <div class="goggle-strap-link right"></div>
              <div class="goggle-frame left">
                <div class="goggle">
                  <div class="eye"></div>
                </div>
              </div>
              <div class="goggle-frame right">
                <div class="goggle">
                  <div class="eye"></div>
                </div>
              </div>
              <div class="goggle-shadow left"></div>
              <div class="goggle-shadow right"></div>
            </div>
          </div>
        </div>
      </section>
    )
  );

  function eyeball() {
    const eye = document.querySelectorAll(".eye");
    eye.forEach(function (eye) {
      let x = eye.getBoundingClientRect.left + eye.clientWidth / 2;
      let y = eye.getBoundingClientRect.top + eye.clientHeight / 2;
      let radian = Math.atan2(x, y);
      let rotation = radian * (180 / Math.PI) * -1 + 270;
      eye.style.transform = "rotate(" + rotation + "deg)";
      console.log("rotate eyes");
    });
  }
}

export default MyDropzone;
